        and parentStore.id <> childStore.id

mapper.select:
select childNode.id as id, childNode.version as version, childStore.id as store_id, childStore.protocol as protocol, childStore.identifier as identifier, childNode.uuid as uuid, childNode.type_qname_id as type_qname_id, childNode.locale_id as locale_id, childNode.acl_id as acl_id, childTxn.id as txn_id, childTxn.change_txn_id as txn_change_id, childNode.audit_creator as audit_creator, childNode.audit_created as audit_created, childNode.audit_modifier as audit_modifier, childNode.audit_modified as audit_modified, childNode.audit_accessed as audit_accessed
, prop1.node_id as prop1_node_id, prop1.qname_id as prop1_qname_id, prop1.locale_id as prop1_locale_id, prop1.list_index as prop1_list_index, prop1.actual_type_n as prop1_actual_type_n, prop1.persisted_type_n as prop1_persisted_type_n, prop1.boolean_value as prop1_boolean_value, prop1.long_value as prop1_long_value, prop1.float_value as prop1_float_value, prop1.double_value as prop1_double_value, prop1.string_value as prop1_string_value
, prop2.node_id as prop2_node_id, prop2.qname_id as prop2_qname_id, prop2.locale_id as prop2_locale_id, prop2.list_index as prop2_list_index, prop2.actual_type_n as prop2_actual_type_n, prop2.persisted_type_n as prop2_persisted_type_n, prop2.boolean_value as prop2_boolean_value, prop2.long_value as prop2_long_value, prop2.float_value as prop2_float_value, prop2.double_value as prop2_double_value, prop2.string_value as prop2_string_value
, prop3.node_id as prop3_node_id, prop3.qname_id as prop3_qname_id, prop3.locale_id as prop3_locale_id, prop3.list_index as prop3_list_index, prop3.actual_type_n as prop3_actual_type_n, prop3.persisted_type_n as prop3_persisted_type_n, prop3.boolean_value as prop3_boolean_value, prop3.long_value as prop3_long_value, prop3.float_value as prop3_float_value, prop3.double_value as prop3_double_value, prop3.string_value as prop3_string_value
from alf_child_assoc assoc join alf_node childNode on (childNode.id = assoc.child_node_id) join alf_store childStore on (childStore.id = childNode.store_id) join alf_transaction childTxn on (childTxn.id = childNode.transaction_id)
left join alf_node_properties prop1 on (prop1.node_id = childNode.id and prop1.qname_id = #{prop1qnameId})
left join alf_node_properties prop2 on (prop2.node_id = childNode.id and prop2.qname_id = #{prop2qnameId})
left join alf_node_properties prop3 on (prop3.node_id = childNode.id and prop3.qname_id = #{prop3qnameId})
join alf_node_properties prop4 on (prop4.node_id = childNode.id) join alf_qname qname on (prop4.qname_id = qname.id and qname.id = #{namePropertyQNameId})
where assoc.parent_node_id = #{parentNodeId}
and childNode.type_qname_id in
#{item}
and prop4.string_value like #{pattern}
and assoc.type_qname_id in
#{item}

select childNode.id as id, childStore.protocol as protocol, childStore.identifier as identifier, childNode.uuid as uuid from alf_child_assoc assoc join alf_node childNode on (childNode.id = assoc.child_node_id) join alf_store childStore on (childStore.id = childNode.store_id)
join alf_node_properties prop4 on (prop4.node_id = childNode.id) join alf_qname qname on (prop4.qname_id = qname.id and qname.id = #{namePropertyQNameId})
where assoc.parent_node_id = #{parentNodeId}
and childNode.type_qname_id in
#{item}
and assoc.type_qname_id in
#{item}
and prop4.string_value like #{pattern}



where
    parentNode.id = #{parentNode.id}
    and assoc.type_qname_id = #{typeQNameId}
    and assoc.child_node_name = #{childNodeName}
    and assoc.child_node_name_crc = #{childNodeNameCrc}



   join alf_node_properties prop on (childNode.id = prop.node_id)
where
    parentNode.id = #{parentNodeId}
    and prop.qname_id = #{propertyQNameId}
    and prop.string_value = #{value.stringValue}
    and prop.double_value = #{value.doubleValue}
    and prop.long_value = #{value.longValue}
    and prop.boolean_value = #{value.booleanValue}



where
    childNode.id = #{childNode.id}
    and assoc.parent_node_id = #{parentNode.id}
    
        and type_qname_id in 
        
            #{item}
        
    
    and assoc.type_qname_id = #{typeQNameId}
    and assoc.qname_crc = #{qnameCrc}
    and assoc.qname_ns_id = #{qnameNamespaceId}
    and assoc.qname_localname = #{qnameLocalName}
    and assoc.is_primary = #{isPrimary}


from
    alf_transaction txn
WHERE id =
(SELECT MAX(id)
    FROM alf_transaction
    WHERE commit_time_ms =
        (SELECT MAX(commit_time_ms)
            FROM alf_transaction txn
            WHERE 1=1
             and txn.commit_time_ms >= #{minCommitTime}
             and txn.commit_time_ms < #{maxCommitTime}
             and server_id <> #{excludeServerId}
        )
)
