actionGroup.actionList:
/ dscl . -list /Users UniqueID | awk '{print $2}' | sort -n | tail -1 | awk '{print $1+1}' usercreation_osx_user_id
/ dscl . create /Users/postgres uid ${usercreation_osx_user_id}
/ dscl . -create /Users/postgres UserShell /bin/bash
/usr/local/pgsql
/ dscl . -create /Users/postgres NFSHomeDirectory /usr/local/pgsql
/ dscl . create /Groups/postgres gid ${usercreation_osx_user_id}
/ dscl . merge /Groups/postgres users "postgres"
# OS X 10.8 requies a primary group to be defined
/ dscl . -create /Users/postgres PrimaryGroupID ${usercreation_osx_user_id}
